// import filtersReducer from '../components/Filters/FilterSlice'
// import todoListReducer from '../components/TodoList/TodoListSlice'
const initState = {
    filters: {
        search :'',
        status : 'All',
        priority : []
    },
    todoList: [
            {id: 1, courseName: 'Learn React', completed: false, priority:'High'},
            {id: 2, courseName: 'Learn JavaScript', completed: true, priority:'Medium'},
            {id: 3, courseName: 'Learn Redux', completed: false, priority:'Low'},
    ]
}
const rootReducer = (state = initState , action) =>{
     console.log({state, action})
 
        // filters:  filtersReducer(state.filters,action),
        // todoList: todoListReducer(state.todoList, action)
        switch(action.type) {
            case 'todoList/addTodo':
                return {
                    ...state,
                    todoList: [
                        ...state.todoList,
                        action.payload
                    ]
                }
            case 'filters/searchFilterChange':
                return {
                    ...state,
                    filters: {
                        ...state.filters,
                        search:  action.payload
                    }
                }
                default:
                    return state
        }
}
export default rootReducer
