const initState = {
    filters: {
        search :'',
        status : 'All',
        priority : []
    },
    todoList:[
        {
            id: '1',
            courseName:'Learn React',
            completed: false,
            priority: 'High',

        },
        {
            id: '2',
            courseName:'Learn Javascript',
            completed: true,
            priority: 'Medium',

        },
        {
            id: '3',
            courseName:'Learn Redux',
            completed: false,
            priority: 'Low',

        }
    ]
}
 const rootReducer = (state = initState, action) =>{
    console.log({state, action})
    switch(action.type){
        case 'todoList/addTodo':
            return {
                ...state,
                todoList: [
                    ...state.todoList,
                    action.payload
                ]
            }
            default: 
                return state;
    }
}
export default rootReducer
