const initState = {
    filters: {
        search :'',
        status : 'All',
        priority : []
    }
}
 const reducerFilter = (state = initState, action) =>{
    switch(action.type){
        case 'filters/searchTodoList':
            return {
                ...state,
                search: [
                    ...state.filters,
                    action.payload
                ]
                }    
            default: 
                return state;
    }
}
export default reducerFilter
